const UserProfile = ({userInfo}) => {
  return (
    <div key={userInfo.id} className="d-flex justify-content-between align-items-center">
      <div>
        <strong>{userInfo.first_name} {userInfo.last_name}</strong>
        <p>Email: {userInfo.email}</p>
      </div>
      <img src={userInfo.avatar} alt="" style={{ borderRadius: '50%' }} />
    </div>
  );

};

UserProfile.getInitialProps = async (ctx) => {
  const res = await fetch(`https://reqres.in/api${ctx.pathname}`);
  const json_response = await res.json();
  return { users: json_response.data };
}

export default UserProfile;
